import React, { useEffect, useRef, useState } from 'react';
import { Line } from 'react-chartjs-2'; // Import Line from react-chartjs-2
import 'chartjs-plugin-zoom';

const ChartSection = ({ data }) => {
  const chartRef = useRef(null);

  useEffect(() => {
    if (chartRef && chartRef.current) {
      const countries = [...new Set(data.map((item) => item.country.value))];

      const datasets = countries.map((country, index) => {
        const color = `rgba(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255}, 1)`;

        return {
          label: country,
          data: data
            .filter((item) => item.country.value === country)
            .map((item) => ({
              x: item.date,
              y: item.value,
            })),
          borderColor: color,
          borderWidth: 1,
          fill: false,
          pointStyle: 'circle',
          pointRadius: 2,
          pointBorderColor: color,
          pointBackgroundColor: color,
        };
      });

      const minYear = Math.min(...data.map((item) => item.date));
      const maxYear = Math.max(...data.map((item) => item.date));

      const minPopulation = Math.min(...data.map((item) => item.value));
      const maxPopulation = Math.max(...data.map((item) => item.value));

      const options = {
        responsive: true,
        plugins: {
          zoom: {
            pan: {
              enabled: true,
              mode: 'xy',
            },
            zoom: {
              enabled: true,
              mode: 'xy',
              sensitivity: 0.02,
            },
          },
        },
        scales: {
          x: {
            type: 'linear',
            min: minYear,
            max: maxYear,
          },
          y: {
            type: 'linear',
            min: minPopulation - minPopulation / 5,
            max: maxPopulation,
          },
        },
      };

      const dataConfig = {
        labels: data.map((item) => item.date),
        datasets: datasets,
      };

      return (
        <Line ref={chartRef} data={dataConfig} options={options} />
      );
    }
  }, [data]);

  return (
    <div className="container mt-5">
      <h2 className="text-center">Chart Section</h2>
      <div className="row">
        <div className="col-md-12">
          {chartRef && chartRef.current ? (
            <Line data={chartRef.current.props.data} options={chartRef.current.props.options} />
          ) : null}
        </div>
      </div>
    </div>
  );
};

export default ChartSection;
