import React, { useState, useEffect, useRef, useCallback } from 'react';
import Chart from 'chart.js/auto';
import 'chartjs-plugin-annotation';

const ChartSection = ({ data }) => {
  const chartRef = useRef(null);
  const chartInstanceRef = useRef(null);

  const [selectedYear, setSelectedYear] = useState(null);
  const [datasets, setDatasets] = useState([]);

  const updateChartSize = useCallback(() => {
    if (chartRef && chartRef.current) {
      const parentWidth = chartRef.current.parentElement.clientWidth;
      chartRef.current.width = parentWidth;
      if (chartInstanceRef.current) {
        chartInstanceRef.current.resize();
      }
    }
  }, []);

  useEffect(() => {
    if (chartInstanceRef.current) {
      chartInstanceRef.current.destroy();
    }

    if (chartRef && chartRef.current) {
      const ctx = chartRef.current.getContext('2d');

      const countries = [...new Set(data.map((item) => item.country.value))];
      const newDatasets = countries.map((country, index) => {
        const color = `rgba(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255}, 1)`;

        return {
          label: country,
          data: data
            .filter((item) => item.country.value === country)
            .map((item) => ({
              x: item.date,
              y: item.value,
            })),
          borderColor: color,
          borderWidth: 1,
          fill: false,
          pointStyle: 'circle',
          pointRadius: 2,
          pointBorderColor: color,
        };
      });

      setDatasets(newDatasets);

      const minYear = Math.min(...data.map((item) => item.date));
      const maxYear = Math.max(...data.map((item) => item.date));

      const populationValues = data.map((item) => item.value);
      const minPopulation = Math.min(...populationValues);
      const maxPopulation = Math.max(...populationValues);

      const newChartInstance = new Chart(ctx, {
        type: 'line',
        data: {
          labels: data.map((item) => item.date),
          datasets: newDatasets,
        },
        options: {
          responsive: true,
          animationEnabled: true,
          zoomEnabled: true,
          scales: {
            x: {
              type: 'linear',
              min: minYear,
              max: maxYear,
              beginAtZero: true,
            },
            y: {
              type: 'linear',
              min: minPopulation - minPopulation / 5,
              max: maxPopulation,
              beginAtZero: true,
            },
          },
        },
      });

      chartInstanceRef.current = newChartInstance;
    }
  }, [data]);

  useEffect(() => {
    if (chartInstanceRef.current) {
      chartInstanceRef.current.options.onClick = (event, elements) => {
        if (elements.length > 0) {
          const year = chartInstanceRef.current.data.labels[elements[0].index];
          setSelectedYear(year);
        }
      };
    }
  }, []);

  useEffect(() => {
    if (selectedYear) {
      const annotations = datasets.map((dataset, index) => ({
        type: 'line',
        mode: 'vertical',
        scaleID: 'x',
        value: selectedYear,
        borderColor: dataset.borderColor,
        borderWidth: 1,
        label: {
          content: `${dataset.label}: ${getPopulationForYear(dataset.label, selectedYear)}`,
          enabled: true,
          position: 'top',
        },
      }));

      chartInstanceRef.current.options.plugins.annotation = {
        annotations,
      };

      chartInstanceRef.current.update();
    }
  }, [selectedYear, datasets]);

  const getPopulationForYear = (country, year) => {
    const countryData = data.filter((item) => item.country.value === country);
    const yearData = countryData.find((item) => item.date === year);

    return yearData ? yearData.value : 'N/A';
  };

  useEffect(() => {
    window.addEventListener('resize', updateChartSize);
    return () => {
      window.removeEventListener('resize', updateChartSize);
    };
  }, [updateChartSize]);

  return (
    <div className="container mt-5">
      <h2 className="text-center">Chart Section</h2>
      <div className="row">
        <div className="col-md-12">
          {selectedYear && (
            <div className="text-center">
              <p>Selected Year: {selectedYear}</p>
            </div>
          )}
          <canvas ref={chartRef}></canvas>
        </div>
      </div>
    </div>
  );
};

export default ChartSection;
