import React, { useState, useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';
import 'chartjs-plugin-zoom';

const ChartSection = ({ data }) => {
  const chartRef = useRef(null);
  const chartInstanceRef = useRef(null);
  const [yAxisMin, setYAxisMin] = useState(0);
  const [yAxisMax, setYAxisMax] = useState(0);

    // Calculate the min and max year values
            const minYear = Math.min(...data.map((item) => item.date));
            const maxYear = Math.max(...data.map((item) => item.date));
      
           // Find the minimum and maximum population values in the data
           const populationValues = data.map((item) => item.value);
           const minPopulation = Math.min(...populationValues);
           const maxPopulation = Math.max(...populationValues);
      
           // Adjust the y-axis boundaries
           const yAxisPadding = (maxPopulation - minPopulation) / 10;
           setYAxisMin(minPopulation - yAxisPadding);
           setYAxisMax(maxPopulation + yAxisPadding);


  useEffect(() => {
    // Check if a previous chart instance exists and destroy it
    if (chartInstanceRef.current) {
      chartInstanceRef.current.destroy();
    }

    if (chartRef && chartRef.current) {
      const ctx = chartRef.current.getContext('2d');

      const countries = [...new Set(data.map((item) => item.country.value))]; // Get unique country values

      const datasets = countries.map((country, index) => ({
        label: country,
        data: data
          .filter((item) => item.country.value === country)
          .map((item) => ({
            x: item.date,
            y: item.value,
          })),
        borderColor: `rgba(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255}, 1)`,
        borderWidth: 2,
        fill: false,
      }));

          
      
      

      const newChartInstance = new Chart(ctx, {
        type: 'line',
        data: {
          labels: data.map((item) => item.date),
          datasets: datasets,
        },
        options: {
          responsive: true,
          scales: {
            x: {
              type: 'linear',
              min: minYear, // Set the minimum year
              max: maxYear, // Set the maximum year
              beginAtZero: true,
            },
            y: {
              type: 'linear',
              min: yAxisMin, // Set the adjusted y-axis minimum
              max: yAxisMax, // Set the adjusted y-axis maximum
              beginAtZero: true,
            },

          },
          plugins: {
            zoom: {
              zoom: {
                enabled: true,
                mode: 'xy',
                speed: 0.05,
              },
            },
          },
        },
      });

      chartInstanceRef.current = newChartInstance;
    }
  }, [data]);

  return (
    <div className="container mt-5">
      <h2 className="text-center">Chart Section</h2>
      <div className="row">
        <div className="col-md-12">
          <canvas ref={chartRef}></canvas>
        </div>
      </div>
    </div>
  );
};

export default ChartSection;
