function App() {
  const [filteredData, setFilteredData] = useState(initialData);

  const handleFilter = (filter) => {
    // Apply the filter logic based on selected countries and years
    const { countries, fromYear, toYear } = filter;

    const filteredResult = initialData.filter((data) => {
      // Check if the country is in the selected countries list
      const isCountryMatch = countries.includes(data.country.value);

      // Check if the year is within the selected year range
      const isYearMatch = parseInt(data.date) >= fromYear && parseInt(data.date) <= toYear;

      return isCountryMatch && isYearMatch;
    });

    setFilteredData(filteredResult);
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">Data Visualization</h1>
      <div className="row">
        <div className="col-md-6">
          <FilterComponent onFilter={handleFilter} />
        </div>

        <div className="col-md-6">
          <ChartSection data={filteredData} />
        </div>

        <div className="col-md-6">
          <TableComponent data={filteredData} />
        </div>
      </div>
    </div>
  );
}

export default App;
