import React, { useEffect, useRef, useState } from 'react';
import { Line } from 'react-chartjs-2'; // Import Line from react-chartjs-2
import 'chartjs-plugin-zoom';

const ChartSection = ({ data }) => {
  const chartRef = useRef(null);
  const chartInstanceRef = useRef(null);
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    // Destroy the previous chart instance
    if (chartInstanceRef.current) {
      chartInstanceRef.current.destroy();
    }

    if (data && data.length > 0) {
      const countries = [...new Set(data.map((item) => item.country.value))];
      const datasets = countries.map((country) => {
        const color = `rgba(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255}, 1)`;
        const countryData = data
          .filter((item) => item.country.value === country)
          .map((item) => ({
            x: item.date,
            y: item.value,
          }));

        return {
          label: country,
          data: countryData,
          borderColor: color,
          borderWidth: 1,
          fill: false,
        };
      });

      const chartConfig = {
        labels: data.map((item) => item.date),
        datasets: datasets,
      };

      setChartData(chartConfig);
    }
  }, [data]);

  const options = {
    responsive: true,
    plugins: {
      zoom: {
        pan: {
          enabled: true,
          mode: 'xy',
        },
        zoom: {
          enabled: true,
          mode: 'xy',
          sensitivity: 0.02,
        },
      },
    },
    scales: {
      x: {
        type: 'linear',
        position: 'bottom',
        beginAtZero: true,
      },
      y: {
        type: 'linear',
        position: 'left',
        beginAtZero: true,
      },
    },
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center">Chart Section</h2>
      <div className="row">
        <div className="col-md-12">
          {chartData && (
            <Line ref={chartRef} data={chartData} options={options} />
          )}
        </div>
      </div>
    </div>
  );
};

export default ChartSection;
