import React, { useState } from 'react';

const FilterComponent = ({ onFilter }) => {
  const [selectedCountries, setSelectedCountries] = useState([]);
  const currentYear = new Date().getFullYear();
  const [fromYear, setFromYear] = useState(1960);
  const [toYear, setToYear] = useState(currentYear - 1);

  const handleCountryChange = (e) => {
    const selectedCountry = e.target.value;
    setSelectedCountries([selectedCountry]);
  };

  const handleFilter = () => {
    const filter = {
      countries: selectedCountries,
      fromYear,
      toYear,
    };

    onFilter(filter);
  };

  return (
    <div className="container mt-3">
      <h2>Filter Data</h2>
      <div className="row">
        <div className="col-md-4">
          <label>Country:</label>
          <select
            className="form-control"
            value={selectedCountries[0]}
            onChange={handleCountryChange}
          >
            <option value="">Select a Country</option>
            <option value="Brazil">Brazil</option>
            <option value="Russian Federation">Russian Federation</option>
            <option value="India">India</option>
            <option value="China">China</option>
            <option value="South Africa">South Africa</option>
          </select>
        </div>
        <div className="col-md-4">
          <label>From Year:</label>
          <select
            className="form-control"
            value={fromYear}
            onChange={(e) => setFromYear(parseInt(e.target.value))}
          >
            {Array.from({ length: currentYear - 1959 }, (_, index) => (
              <option key={index} value={1960 + index}>
                {1960 + index}
              </option>
            ))}
          </select>
        </div>
        <div className="col-md-4">
          <label>To Year:</label>
          <select
            className="form-control"
            value={toYear}
            onChange={(e) => setToYear(parseInt(e.target.value))}
          >
            {Array.from({ length: currentYear - 1959 }, (_, index) => (
              <option key={index} value={1960 + index}>
                {1960 + index}
              </option>
            ))}
          </select>
        </div>
      </div>
      <button className="btn btn-primary mt-3" onClick={handleFilter}>
        Filter
      </button>
    </div>
  );
};

export default FilterComponent;
