import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';
import 'chartjs-plugin-annotation';

const ChartSection = ({ data }) => {
  const chartRef = useRef(null);
  const chartInstanceRef = useRef(null);

  useEffect(() => {
    if (chartInstanceRef.current) {
      chartInstanceRef.current.destroy(); // Destroy the previous chart instance
    }

    if (chartRef && chartRef.current) {
      const ctx = chartRef.current.getContext('2d');

      const countries = [...new Set(data.map((item) => item.country.value))]; // Get unique country values

      const datasets = countries.map((country, index) => {
        // Generate a unique color for each country
        const color = `rgba(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255}, 1)`;

        return {
          label: country,
          data: data
            .filter((item) => item.country.value === country)
            .map((item) => ({
              x: item.date,
              y: item.value,
            })),
          borderColor: color,
          borderWidth: 1,
          fill: false,
          pointStyle: 'circle',
          pointRadius: 2,
          pointBorderColor: color,
        };
      });

      // Calculate the min and max year values
      const minYear = Math.min(...data.map((item) => item.date));
      const maxYear = Math.max(...data.map((item) => item.date));

      // Find the minimum and maximum population values in the data
      const populationValues = data.map((item) => item.value);
      const minPopulation = Math.min(...populationValues);
      const maxPopulation = Math.max(...populationValues);

      const newChartInstance = new Chart(ctx, {
        type: 'line',
        data: {
          labels: data.map((item) => item.date),
          datasets: datasets,
        },
        options: {
          responsive: true,
          animationEnabled: true,
          zoomEnabled: true,
          scales: {
            x: {
              type: 'linear',
              min: minYear,
              max: maxYear,
              beginAtZero: true,
            },
            y: {
              type: 'linear',
              min: minPopulation - minPopulation / 5,
              max: maxPopulation,
              beginAtZero: true,
            },
          },
          plugins: {
            annotation: {
              drawTime: 'beforeDatasetsDraw', // Ensure that the annotation is drawn before datasets
              annotations: [], // Initialize an empty array for annotations
            },
          },
        },
      });

      chartInstanceRef.current = newChartInstance;
    }
  }, [data]);

  // Function to handle mouse move and draw vertical line
  const handleMouseMove = (event) => {
    if (event.nativeEvent) {
      if (chartInstanceRef.current) {
        const chartInstance = chartInstanceRef.current;
        const ctx = chartInstance.ctx;

        // Clear previous annotations
        chartInstance.options.plugins.annotation.annotations = [];

        // Get the x-axis value (year) at the mouse position
        const xValue = chartInstance.scales.x.getValueForPixel(event.nativeEvent.offsetX);

        // Draw a vertical line at the mouse position
        chartInstance.options.plugins.annotation.annotations.push({
          type: 'line',
          mode: 'vertical',
          scaleID: 'x',
          value: xValue,
          borderColor: 'rgba(0, 0, 0, 0.5)',
          borderWidth: 1,
          borderDash: [5, 5],
        });

        chartInstance.update();
      }
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center">Chart Section</h2>
      <div className="row">
        <div className="col-md-12">
          <canvas ref={chartRef} onMouseMove={handleMouseMove}></canvas>
        </div>
      </div>
    </div>
  );
};

export default ChartSection;
