import React, { useState } from 'react';
import { CSVLink } from 'react-csv';

const TableComponent = ({ data }) => {
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 15;

  const totalPages = Math.ceil(data.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentData = data.slice(startIndex, endIndex);

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  // Prepare data for export
  const exportData = currentData.map((item) => ({
    Country: item.country.value,
    Value: item.value,
    Date: item.date,
  }));

  // Define CSV headers
  const headers = [
    { label: 'Country', key: 'Country',style: {
      fontWeight: 'bold',
      color: 'red',
    },
},
    { label: 'Population', key: 'Value', className: 'font-weight-bold text-danger' },
    { label: 'Year', key: 'Date', className: 'font-weight-bold text-danger' },
  ];

  return (
    <div className="container mt-3">
      <h2>Data Table</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Country</th>
            <th>Population</th>
            <th>Year</th>
          </tr>
        </thead>
        <tbody>
          {currentData.map((item, index) => (
            <tr key={index}>
              <td>{item.country.value}</td>
              <td>{item.value}</td>
              <td>{item.date}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="d-flex justify-content-between">
        <button
          className="btn btn-primary"
          onClick={handlePrevPage}
          disabled={currentPage === 1}
        >
          Previous
        </button>
        <span className="mt-2">
          Page {currentPage} of {totalPages}
        </span>
        <button
          className="btn btn-primary"
          onClick={handleNextPage}
          disabled={currentPage === totalPages}
        >
          Next
        </button>
      </div>
      <CSVLink data={exportData} headers={headers} filename={'data-export.csv'} >
        <button className="btn btn-primary mt-3">Export as CSV</button>
      </CSVLink>
    </div>
  );
};

export default TableComponent;
