import React, { useState } from 'react';

const FilterComponent = ({ onFilter }) => {
  const [selectedCountries, setSelectedCountries] = useState([]);
  const currentYear = new Date().getFullYear();
  const [fromYear, setFromYear] = useState(1960);
  const [toYear, setToYear] = useState(currentYear - 1);

  const handleCountryChange = (e, country) => {
    const isChecked = e.target.checked;
    if (isChecked) {
      setSelectedCountries([...selectedCountries, country]);
    } else {
      setSelectedCountries(selectedCountries.filter((c) => c !== country));
    }
  };

  const handleFilter = () => {
    const filter = {
      countries: selectedCountries,
      fromYear,
      toYear,
    };

    onFilter(filter);
  };

  return (
    <div className="container mt-3">
      <h2>Filter Data</h2>
      <div className="row">
        <div className="col-md-4">
          <label>Countries:</label>
          <div>
            <label>
              <input
                type="checkbox"
                value="Brazil"
                onChange={(e) => handleCountryChange(e, 'Brazil')}
              />
              Brazil
            </label>
            <br />
            <label>
              <input
                type="checkbox"
                value="Russian Federation"
                onChange={(e) => handleCountryChange(e, 'Russian Federation')}
              />
              Russian Federation
            </label>
            <br />
            <label>
              <input
                type="checkbox"
                value="India"
                onChange={(e) => handleCountryChange(e, 'India')}
              />
              India
            </label>
            <br />
            <label>
              <input
                type="checkbox"
                value="China"
                onChange={(e) => handleCountryChange(e, 'China')}
              />
              China
            </label>
            <br />
            <label>
              <input
                type="checkbox"
                value="South Africa"
                onChange={(e) => handleCountryChange(e, 'South Africa')}
              />
              South Africa
            </label>
          </div>
        </div>
        <div className="col-md-4">
          <label>From Year:</label>
          <select
            className="form-control"
            value={fromYear}
            onChange={(e) => setFromYear(parseInt(e.target.value))}
          >
            {Array.from({ length: currentYear - 1959 }, (_, index) => (
              <option key={index} value={1960 + index}>
                {1960 + index}
              </option>
            ))}
          </select>
        </div>
        <div className="col-md-4">
          <label>To Year:</label>
          <select
            className="form-control"
            value={toYear}
            onChange={(e) => setToYear(parseInt(e.target.value))}
          >
            {Array.from({ length: currentYear - 1959 }, (_, index) => (
              <option key={index} value={1960 + index}>
                {1960 + index}
              </option>
            ))}
          </select>
        </div>
      </div>
      <button className="btn btn-primary mt-3" onClick={handleFilter}>
        Filter
      </button>
    </div>
  );
};

export default FilterComponent;
